      </el-form-item>
    </el-form>

    <span
      slot="footer"
      class="dialog-footer"
    >
      <el-button size="large" @click="resetForm">
        取消
      </el-button>
      <el-button
        type="primary"
        size="large"
        @click="submitForm"
      >
        提交
      </el-button>
    </span>
  </el-dialog>
</template>

<script>

export default {
  data() {
    return {
      modalTitle: '新增项',
      dialogVisible: false,
      AddTarget: '',
      isStatus: false,
      isOption: false,
      additionTagsForm: {
        flag: '',
        base64: ''
      },
      additionTagsRules: {
        flag: [
          { required: true, message: '请选择状态标记', trigger: 'change' }
        ]
      }
    }
  },
  watch: {
    'additionTagsForm.flag': function(val) {
      if (!val) {
        this.additionTagsForm.base64 = null
      }
      if (val === '#New') {
        this.getSVGcontent(this.$refs.svgNew)
      }
      if (val === '#Presale') {
        this.getSVGcontent(this.$refs.svgPresale)
      }
      if (val === '#Feature') {
        this.getSVGcontent(this.$refs.svgFeature)
      }
      if (val === '#YearsLowest') {
        this.getSVGcontent(this.$refs.svgYearsLowest)
      }
    }
  },
  methods: {
    getSVGcontent(refEle) {
      this.convertSvgToBase64(refEle)
        .then(base64String => {
          this.additionTagsForm.base64 = base64String
        })
        .catch(error => {
          console.error(error)
        })
    },
    convertSvgToBase64(SVG) {
      // 获取 SVG 元素
      const svgElement = SVG
      // 将 SVG 元素转换为 Blob 对象
      const svgBlob = new Blob([svgElement.outerHTML], { type: 'image/svg+xml' })
      // 通过 Blob 对象创建 URL
      const svgUrl = URL.createObjectURL(svgBlob)
      // 使用 fetch() 获取 SVG 文件的内容
      return fetch(svgUrl)
        .then(response => response.blob())
        .then(blob => {
          // 将 Blob 对象转换为 Base64 字符串
          return new Promise((resolve, reject) => {
            const reader = new FileReader()
            reader.onloadend = () => {
              const base64String = reader.result
              resolve(base64String)
            }
            reader.onerror = reject
            reader.readAsDataURL(blob)
          })
        })
    },
    resetForm() {
      this.dialogVisible = false
      this.additionTagsForm.flag = undefined
    },
    submitForm() {
      this.$refs.additionTagsForm.validate(valid => {
        if (!valid) return
        const val = {
          Target: this.AddTarget,
          tag: this.additionTagsForm
        }
        this.$emit('addTag-submit', val)
        this.dialogVisible = false
        this.additionTagsForm.flag = undefined
      })
    },
    conlog() {
      console.log('additionTagsForm', this.additionTagsForm)
    },
    show(val) {
      if (val) {
        if (val.isStatus) {
          this.isOption = false
          this.isStatus = val.isStatus
          this.AddTarget = val.AddTarget
        } else if (val.isOption) {
          this.isStatus = false
          this.isOption = val.isOption
          this.AddTarget = val.AddTarget
        }
      } else {
        this.isStatus = false
        this.isOption = false
        this.modalTitle = '新增项'
      }
      this.dialogVisible = true
    },
    handleClose(done) {
      this.$confirm('确认关闭？')
        .then((_) => {
          done()
        })
        .catch((_) => {})
    }
  }
}
</script>
